    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        int a1=Integer.parseInt(A1.getText());
        int b1=Integer.parseInt(B1.getText());
        int c1=Integer.parseInt(C1.getText());
        int d1=Integer.parseInt(D1.getText());
        int a2=Integer.parseInt(A2.getText());
        int b2=Integer.parseInt(B2.getText());
        int c2=Integer.parseInt(C2.getText());
        int d2=Integer.parseInt(D2.getText());
        int a3=Integer.parseInt(A3.getText());
        int b3=Integer.parseInt(B3.getText());
        int c3=Integer.parseInt(C3.getText());
        int d3=Integer.parseInt(D3.getText());

        int del1=(d1*(b2*c3-b3*c2)-b1*(d2*c3-d3*c2)+c1*(d2*b3-d3*b2));
        int del2=(a1*(d2*c3-d3*c2)-d1*(a2*c3-a3*c2)+c1*(a2*d3-a3*d2));
        int del3=(a1*(b2*d3-b3*d2)-b1*(a2*d3-a3*d2)+d1*(a2*b3-a3*b2));
        int del=(a1*(b2*c3-b3*c2)-b1*(a2*c3-a3*c2)+c1*(a2*b3-b2*a3));
        double res1=(double)del1/del;
        double res2=(double)del2/del;
        double res3=(double)del3/del;
        X.setText(Double.toString(res1));
        Y.setText(Double.toString(res2));
        Z.setText(Double.toString(res3));


    }//GEN-LAST:event_jButton4ActionPerformed

