    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double A1=Double.parseDouble(a1.getText());
        double B1=Double.parseDouble(b1.getText());
        double C1=Double.parseDouble(c1.getText());
        double A2=Double.parseDouble(a2.getText());
        double B2=Double.parseDouble(b2.getText());
        double C2=Double.parseDouble(c2.getText());
        double X=(double)(B1*C2-B2*C1)/(B1*A2-A1*B2);
        double Y=(double)(C1-A1*X)/B1;
        x.setText(Double.toString(X));
        y.setText(Double.toString(Y));
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        // TODO add your handling code here:
        float res1,res2;
        int A=Integer.parseInt(a.getText());
        int B=Integer.parseInt(b.getText());
        int C=Integer.parseInt(c.getText());
        int D=  B*B-4*A*C;
        if(D==0)
        {
          res1=(float)-B/2*A ;
          res2=res1;
          x1.setText(Float.toString(res1));
          x2.setText(Float.toString(res2));

        }
        else if(D>0)
        {
            res1=(float)(-B+Math.sqrt(D))/2*A;
            res2=(float)(-B-Math.sqrt(D))/2*A;
            x1.setText(Float.toString(res1));
            x2.setText(Float.toString(res2));
        }
        else
        {
            res1=(float)(-B+Math.sqrt(Math.abs(D)))/2*A;
            res2=(float)(-B-Math.sqrt(Math.abs(D)))/2*A;
            JOptionPane.showMessageDialog(rootPane,"Roots are Imaginary"+ "\n"+ "X1="+ res1+ "+"+"i"+res2+"\n"+"X2="+ res1 + "-"+ "i"+res2);
           // JOptionPane.showMessageDialog(rootPane,"X2="+ res1 + "-"+ "i"+res2);

        }


    }//GEN-LAST:event_jButton7ActionPerformed

